import { Component } from '@angular/core';
import { FormControl, FormGroup } from '@angular/forms';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {

  isLoginMode : boolean = true;

  loginCredentials : string = '';

  loginForm : FormGroup = new FormGroup({
    email : new FormControl(''),
    password : new FormControl(''),
  });

  onSwitchMode(){
    this.isLoginMode = !this.isLoginMode;
  }
  onLoginSubmit(data : FormGroup){
    console.log(data);
  }

}
<div class="app-login-form">
    <h2 >{{ isLoginMode? 'Login Here' : 'Sign up Here' }}</h2>
    <form ngModel formGroupName="loginForm" (ngSubmit)="onLoginSubmit(loginForm)" [formGroup]="loginForm">
        <div class="form-input">
            <label>Email Address</label>
            <input type="email" placeholder="Email address" formControlName="email">
        </div>
        <div class="form-input">
            <label>Password</label>
            <input type="password" placeholder="Password" formControlName="password">
        </div>           
        <button type="submit" class="app-login-btn">{{ isLoginMode? 'Login' : 'Sign up' }}</button>
        <p>{{ !isLoginMode? 'Already having an account? ':'Do not have an accout yet? ' }} 
            <button class="app-toggle-btn" type="button" (click)="onSwitchMode()">{{ !isLoginMode? 'Login' : 'Register' }}</button>
        </p>
    </form>
</div>
