import { Component, OnInit, inject } from '@angular/core';
import { Task } from '../model/task';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { map } from 'rxjs';
import { TaskService } from '../services/Task.service';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css']
})
export class DashboardComponent implements OnInit {
  showCreateTaskForm: boolean = false;

  http: HttpClient = inject(HttpClient);

  allTasks: Task[] = [];

  taskService: TaskService = inject(TaskService);

  editMode: boolean = false;

  selectedTask: Task;
  currentTaskId : string;

  isLoading : boolean = false;

  errorMessage : string | null = null;
  ngOnInit(): void {
    this.fetchAllTask();
  }
  OpenCreateTaskForm() {
    this.showCreateTaskForm = true;
    this.editMode = false;
    this.selectedTask = {
      title: '', description: '', assignedTo: '', createdAt: '', priority: '', status: '', id: ''
    }
  }

  CloseCreateTaskForm() {
    this.showCreateTaskForm = false;
  }

  createOrUpdateTask(form: Task) {
    // console.log("Task Created", form);
    // // this.http.post('https://angular-httpclient-42340-default-rtdb.asia-southeast1.firebasedatabase.app/tasks.json', form)
    // // .subscribe( (response)=>{
    // //   console.log(response);
    // // });

    // const headers = new HttpHeaders({ 'my-header': 'my-header-value' })
    // this.http.post<{ name: string }>('https://angular-httpclient-42340-default-rtdb.asia-southeast1.firebasedatabase.app/tasks.json', form, { headers: headers })
    //   .subscribe((response) => {
    //     console.log(response);
    //   });
    //   this.fetchAllTask();


    if(this.editMode){
      this.taskService.updateTask(this.currentTaskId, form);
    }else{
      this.taskService.createTask(form);
    }
  }

  private fetchAllTask() {
    // // this.http.get('https://angular-httpclient-42340-default-rtdb.asia-southeast1.firebasedatabase.app//tasks.json')
    // // .subscribe( (response) =>{
    // //   console.log(response);
    // // })

    // this.http.get<{ [key: string]: Task }>('https://angular-httpclient-42340-default-rtdb.asia-southeast1.firebasedatabase.app//tasks.json')
    //   .pipe(map((response) => {
    //     //transform data
    //     let tasks = [];

    //     for (let key in response) {
    //       if (response.hasOwnProperty(key)) {
    //         tasks.push({ ...response[key], id: key });
    //       }
    //     }
    //     return tasks;
    //   }))
    //   .subscribe((response) => {
    //     this.allTasks = response;
    //     // console.log(response);
    //   })
    //   this.fetchAllTask();
    this.isLoading = true;
    this.taskService.getAllTasks().subscribe( {next : (task: Task[]) => {
      this.allTasks = task; 
      this.isLoading = false;
    },
    error : (error) =>{
      this.errorMessage = error.message;
      this.isLoading = false;
      setTimeout(() => {
        this.errorMessage = null;
      }, 3000);
    },
  }

  )

  }
  fetchAllTasksToDisplay() {
    this.fetchAllTask();
  }

  deleteTask(key: string) {
    this.taskService.deleteTask(key);
    this.fetchAllTask();
  }
  clearAllTasks() {
    this.taskService.deleteAllTasks();
    this.fetchAllTask();
  }

  onEditClassClick(key: string) {
    // this.taskService.editTask(key);
    this.showCreateTaskForm = true;
    this.editMode = true;
    // console.log(key);

    this.selectedTask = this.allTasks.find((task) => {
      return task.id === key;
    })

    this.currentTaskId = key;
  }
}
<app-create-task 
        *ngIf="showCreateTaskForm" 
        (CloseForm)="CloseCreateTaskForm()"
        (EmitTaskData) = "createOrUpdateTask($event)"
        [isEditMode]="editMode"
        [selectedTask]="selectedTask"
></app-create-task>

<div class="dashboard-container">
    <div style="display: flex;">
        <div style="width: 50%">
            <h2>Dashboard</h2>
        </div>
        <div style="width: 50%; text-align: right;">
            <button class="create-task-btn" (click)="OpenCreateTaskForm()">Create Task</button>
            <button class="create-task-btn" (click)="fetchAllTasksToDisplay()">Fetch Tasks</button>
            <button class="create-task-btn" (click)="clearAllTasks()">Clear Tasks</button>
        </div>
    </div>

    <hr>

    <div class="dashboard-item-container">
        <div class="sb sb-error">{{ errorMessage }}</div>
        <div class="loader" *ngIf="isLoading"></div>
        <div class="no-task" style="text-align : center" *ngIf="allTasks.length<1" >
            <h2 >No Task has been created yet !</h2>
        </div>
            <ng-container *ngIf="!isLoading">
                <div class="task-item" [ngClass]="'task-'+ task.status" *ngFor="let task of allTasks">
                    <div class="task-title">
                        <h3>{{ task.title }}</h3>
                    </div>
                    <p><b>Assigned to: </b>{{ task.assignedTo }}</p>
                    <p><b>Priority: </b>{{ task.priority }}</p>
                    <p><b>Status: </b>{{ task.status }}</p>
                    <div class="task-action-icons">
                        <button class="task-action-btn" title="Detail" >
                            <i class="fa fa-info-circle" aria-hidden="true"></i>
                        </button>
                        <button class="task-action-btn" title="Edit" (click)="onEditClassClick(task.id)">
                            <i class="fa fa-pencil-square-o" aria-hidden="true"></i>
                        </button>
                        <button class="task-action-btn" title="Delete" (click)="deleteTask(task.id)">
                            <i class="fa fa-trash" aria-hidden="true"></i>
                        </button>            
                    </div>
                </div>
            </ng-container>

           
    </div>
</div>
